package strings

import (
	"testing"

	"github.com/stretchr/testify/assert"
)

// go test -v -count=1 ./strings/ -run ^TestShortestPalindrome$
func TestShortestPalindrome(t *testing.T) {
	for _, tc := range []struct {
		s          string
		palindrome string
	}{
		{s: "aacecaaa", palindrome: "aaacecaaa"},
		{s: "abcd", palindrome: "dcbabcd"},
		{s: "aabba", palindrome: "abbaabba"},
		{s: "abb", palindrome: "bbabb"},
		{s: "", palindrome: ""},
		{s: "a", palindrome: "a"},
		{s: "zz", palindrome: "zz"},
		// {s: "", palindrome: ""},
	} {
		palindrome := shortestPalindrome(tc.s)
		assert.Equal(t, tc.palindrome, palindrome)
	}
}
