package strings

import (
	"testing"

	"github.com/stretchr/testify/assert"
)

// go test -v -count=1 ./strings/ -run ^TestLongestDupSubstring$
func TestLongestDupSubstring(t *testing.T) {
	for _, tc := range []struct {
		s      string
		result string
	}{
		{s: "banana", result: "ana"},
		{s: "abcd", result: ""},
		{s: "", result: ""},
	} {
		result := longestDupSubstring(tc.s)
		assert.Equal(t, tc.result, result)
	}
}
